              {redAuthToken.role === "Job Provider" && (
                <>
                  <li className="nav-item">
                    <NavLink
                      className="nav-link"
                      to="/manage-applicants"
                      activeClassName="active"
                    >
                      Applicant
                    </NavLink>
                  </li>
                  <li className="nav-item">
                    <NavLink
                      className="nav-link"
                      to="/manage-jobs"
                      activeClassName="active"
                    >
                      Jobs
                    </NavLink>
                  </li>
