                <>
                  <li className="nav-item">
                    <NavLink
                      className="nav-link"
                      to="/dashboard"
                      activeClassName="active"
                    >
                      Việc làm
                    </NavLink>
                  </li>
                  <li className="nav-item">
                    <NavLink
                      className="nav-link"
                      to="/appliedJobs"
                      activeClassName="active"
                    >
                      Việc đã nộp
                    </NavLink>
                  </li>
                </>
              )}
              <li className="nav-item dropdown">
              <Nav>
  <Dropdown align={"end"} className={classes.dropDown}>
    <Dropdown.Toggle
      className={`nav-link dropdown-toggle ${classes.user}`}
      id="navbarDropdownMenuLink"
      role="button"
      data-bs-toggle="dropdown"
      aria-expanded="false"
    >
      <span className={classes.username}>
        <i className="bi bi-person-circle"></i> {redAuthToken.userName}
      </span>
    </Dropdown.Toggle>
    <Dropdown.Menu className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
      <Link to="/change-password" className={`dropdown-item ${classes.changePassword}`}>
        Đổi mật khẩu
      </Link>
      <Dropdown.Divider />
      <Dropdown.Item
        as={"button"}
        onClick={logoutHandler}
        className={`dropdown-item ${classes.changePassword}`}
      >
        Đăng xuất
      </Dropdown.Item>
    </Dropdown.Menu>
  </Dropdown>
</Nav>
              </li>
            </ul>
            {/* Left links */}
          </div>
          {/* Collapsible wrapper */}
        </div>
        {/* Container wrapper */}
      </nav>
      {/* Navbar */}
    </>
  );
};

export default Navigation;
