import React from "react";
import { Link, useNavigate, NavLink } from "react-router-dom";
import { Navbar, Container, Nav, Dropdown } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import jwtDecode from "jwt-decode";

function Navigation() {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const authToken = useSelector((state) => state.auth.token); // Get token from Redux store
  const decodedToken = authToken ? jwtDecode(authToken) : null;

  const logoutHandler = () => {
    dispatch({ type: "CLEARAUTHTOKEN" });
    navigate("/", { replace: true });
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top">
      <div className="container">
        <NavLink className="navbar-brand" to="/dashboard">
          <i className="fab fa-linkedin fa-2x" />
        </NavLink>

        {/* ... (Search form - You might need to adjust based on your requirements) ... */}

        <Navbar.Toggle aria-controls="navbarSupportedContent" />
        <Navbar.Collapse id="navbarSupportedContent">

          {/* Conditional Links Based on User Role */}
          {decodedToken && (
            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
              {decodedToken.role === "Admin" && (
                <>
                  <li className="nav-item">
                    <NavLink className="nav-link" to="/manage-users">Người dùng</NavLink>
                  </li>
                  {/* Add other admin links here */}
                </>
              )}
              {decodedToken.role === "Job Provider" && (
                <>
                  <li className="nav-item">
                    <NavLink className="nav-link" to="/manage-applicants">Applicant</NavLink>
                  </li>
                  {/* Add other job provider links here */}
                </>
              )}
              {decodedToken.role === "User" && (
                <>
                  <li className="nav-item">
                    <NavLink className="nav-link" to="/dashboard">Apply</NavLink>
                  </li>
                  {/* Add other user links here */}
                </>
              )}
            </ul>
          )}

          {/* User Dropdown */}
          {decodedToken && (
            <Nav>
              <Dropdown align="end">
                <Dropdown.Toggle>
                  <span>
                    <i className="fas fa-user-circle"></i> {decodedToken.userName}
                  </span>
                </Dropdown.Toggle>
                <Dropdown.Menu>
                  <Link to="/change-password" className="dropdown-item">Đổi mật khẩu</Link>
                  <Dropdown.Divider />
                  <Dropdown.Item onClick={logoutHandler}>Đăng xuất</Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            </Nav>
          )}
        </Navbar.Collapse>
      </div>
    </nav>
  );
};


export default Navigation;
